import "./App.css";
import { Routes, Route } from "react-router-dom";
import { Home } from "./components/Home/Home";
import { Start } from "./components/Start/Start";
import { Nav } from "./components/Nav/Nav";
import axios from "axios";
import { useState } from "react";

function App() {
  // const loccation = useLocation();
  const [games, setGames] = useState();

  const onSearch = (name) => {
    const URL_BASE = "http://localhost:3001/videogames";

    if (games.find((game) => game.name === name)) alert("Personaje repetido");

    axios.get(`${URL_BASE}?name=${name}`).then((response) => {
      if (response.data) {
        // setGames((oldGames) => [...oldGames, response.data]);
        setGames([...games, ...response.data]);
        console.log(games);
      } else {
        alert("Algo salio mal");
      }
    });
  };

  return (
    <div className="App">
      {/* {loccation.pathname !== "/" && <Nav onSearch={onSearch} />} */}
      <Nav onSearch={onSearch} />
      <Routes>
        <Route path="/" element={<Start />} />
        <Route path="/Home" element={<Home games={games} />} />
      </Routes>
    </div>
  );
}

export default App;
