import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createGame } from "../../Redux/actions";
import validate from "./validate";

export const CreateGame = () => {
  const dispatch = useDispatch();
  const allGenres = useSelector((state) => state.allGenres);
  const allPlatforms = useSelector((state) => state.allPlatforms);

  const [indication, setIndication] = useState({
    name: "",
    background_image: "",
    description: "",
    platforms: [],
    released: "",
    rating: 0,
    genres: [],
  });

  const [errors, setErrors] = useState({
    name: "",
    background_image: "",
    description: "",
    platforms: [],
    released: "",
    rating: 0,
    genres: [],
  });

  const handleInputChange = (event) => {
    const property = event.target.name;
    const value = event.target.value;

    if (property === "genres") {
      setIndication({
        ...indication,
        genres: [...indication.genres, event?.target?.value],
      });
    } else if (property === "platforms") {
      setIndication({
        ...indication,
        platforms: [...indication.platforms, event?.target.value],
      });
    }

    setIndication({ ...indication, [property]: value });

    setErrors(validate({ ...indication, [property]: value }, errors));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(createGame(indication));
  };

  const selectGenreHandler = (event) => {
    event?.preventDefault();

    if (!indication.genres.includes(event?.target?.value)) {
      setIndication({
        ...indication,
        genres: [...indication.genres, event?.target?.value],
      });
    }
  };

  const selectPlatformHandler = (event) => {
    event?.preventDefault();

    if (!indication.platforms.includes(event?.target.value)) {
      setIndication({
        ...indication,
        platforms: [...indication.platforms, event?.target.value],
      });
    }
  };

  console.log(indication);

  return (
    <div>
      <form action="" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="">Nombre:</label>
          <input
            type="text"
            name="name"
            value={indication.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="">Image:</label>
          <input
            type="text"
            name="background_image"
            value={indication.background_image}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="">Description:</label>
          <textarea
            name="description"
            value={indication.description}
            onChange={handleInputChange}
            id=""
            cols="30"
            rows="10"
          ></textarea>
        </div>

        <div>
          <label htmlFor="">platforms:</label>
          <select
            name="platforms"
            value={indication.platforms}
            onChange={handleInputChange}
            onClick={(event) => selectPlatformHandler(event)}
          >
            <option value="Select">Select</option>
            {allPlatforms.map((plat) => (
              <option key={plat.name} value={plat.name}>
                {plat.name}
              </option>
            ))}
          </select>
          {/* {allPlatforms.map((plat) => (
            <label for={plat.name}>
              <input
                type="checkbox"
                value={plat.name}
                key={plat.name}
                onChange={handleInputChange}
                onClick={(e) => selectPlatformHandler(e)}
                name="platforms"
              />
              {plat.name}
            </label>
          ))} */}
        </div>

        <div>
          <label htmlFor="">Released:</label>
          <input
            type="date"
            name="released"
            value={indication.released}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="">Rating:</label>
          <input
            type="number"
            name="rating"
            value={indication.rating}
            onChange={handleInputChange}
          />
        </div>

        <div>
          <label htmlFor="">Genres:</label>

          <select
            name="genres"
            value={indication.genres}
            onChange={handleInputChange}
            onClick={(event) => selectGenreHandler(event)}
          >
            <option value="select">Select</option>
            {allGenres.map((genre) => (
              <option key={genre.id} value={genre.id}>
                {genre.name}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

