import { FILTER_GENRES } from "./actions";
import { GET_GAMES } from "./actions";
import { CREATE_GAME } from "./actions";
import { GAME_DETAIL } from "./actions";
import { ORDER_GAMES } from "./actions";
import { ORDER_RATING } from "./actions";
import { GET_GENRES } from "./actions";

const initialState = {
  allGames: [],
  gameDetail: {},
  allGenres: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_GAMES:
      return {
        ...state,
        allGames: action.payload,
      };

    case GAME_DETAIL:
      return {
        ...state,
        gameDetail: action.payload,
      };

    case GET_GENRES:
      return {
        ...state,
        allGenres: action.payload,
      };

    case FILTER_GENRES:
      // let filteredGenres = [...state.allGames];
      // filteredGenres =
      //   action.payload === "All"
      //     ? state.allGames
      //     : state.allGames.filter((game) => game.genres === action.payload);

      const allVideoGamesG = state.allGames;
      const filteredGenre =
        action.payload === "All"
          ? state.allGames
          : allVideoGamesG.filter((gen) => {
              return !gen.createInDb
                ? gen.genres.includes(action.payload)
                : gen.genres.map((elem) => elem.name).includes(action.payload);
            });

      return {
        ...state,
        allGames: filteredGenre,
      };

    case CREATE_GAME:
      return {
        ...state,
        allGames: [...state.allGames, action.payload],
      };

    case ORDER_GAMES:
      let sortedGames = [...state.allGames];

      sortedGames.sort((a, b) => {
        if (action.payload === "A-Z") {
          return a.name.localeCompare(b.name);
        } else {
          return b.name.localeCompare(a.name);
        }
      });

      return {
        ...state,
        allGames: sortedGames,
      };

    case ORDER_RATING:
      let orderRat = [...state.allGames];

      orderRat.sort((a, b) => {
        if (action.payload === "0-5") {
          return a.rating - b.rating;
        } else if ("5-0") {
          return b.rating - a.rating;
        } else {
          return state.allGames;
        }
      });

      return {
        ...state,
        allGames: orderRat,
      };

    default:
      return { ...state };
  }
};
export default rootReducer;

