import { Link, useLocation } from "react-router-dom";
import { SearchBar } from "../SearchBar/SearchBar";
import { useState } from "react";
import styles from "../Nav/Nav.module.css";
import control from "../../control-xbox.png";
import { CreateGame } from "../CreateGame/CreateGame";

export const Nav = () => {
  let filterOrder = useState(true);
  let isActive = useState(true);

  const location = useLocation();

  if (location.pathname === "/videogames/createGames") {
    filterOrder = false;
  }

  const handleClick = () => {
    <CreateGame />;
  };

  return (
    <div className={styles.nav}>
      <h1>Game Pages</h1>
      <SearchBar />
      {filterOrder ? (
        <button onClick={handleClick} className={styles.botones}>
          Create Game
        </button>
      ) : null}

      {!filterOrder ? (
        <Link to="/Home">
          <button className={styles.botones}>Home</button>
        </Link>
      ) : null}
    </div>
  );
};
