import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import { gameDetails } from "../../Redux/actions";

export const Detail = () => {
  const { id } = useParams();
  const gameDetail = useSelector((state) => state.gameDetail);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(gameDetails(id));
  }, []);

  console.log(gameDetail.genres);

  return (
    <div style={{ backgroundImage: `url(${gameDetail.background_image})` }}>
      {gameDetail.name ? (
        <div>
          <h2>Id: {gameDetail.id} </h2>
          <h2>Name: {gameDetail.name} </h2>
          <h2>Platforms: {gameDetail.platforms}</h2>
          <h2>Description: {gameDetail.description}</h2>
          <h2>Released: {gameDetail.released}</h2>
          <h2>Rating: {gameDetail.rating}</h2>
          <h2>Genres: {gameDetail.genres}</h2>
          {/* <img src={gameDetail.background_image} alt="" /> */}
        </div>
      ) : (
        <div>
          <h2>...Loading</h2>
        </div>
      )}
      <Link to="/Home">
        <button>Home</button>
      </Link>
    </div>
  );
};
