import { FILTER_GENRES } from "./actions";

const initialState = {
  allGames: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.payload) {
    case FILTER_GENRES:
      return {
        ...state,
        allGames: state.allGames.filter((game) => {
          game.genres = action.payload;
        }),
      };

    default:
      return { ...state };
  }
};
export default rootReducer;
