const validate = (indication) => {
  const regex = /^[a-zA-Z_][a-zA-Z0-9_]*$/;
  const errors = {};
  if (indication.name.length > 0 && !regex.test(indication.name)) {
    errors.name = alert("Invalid name");
  }
  if (indication.rating > 5 || indication.rating < 0) {
    errors.indication = alert("Exceed rating score limit");
  }
};
export default validate;
