import { FILTER_GENRES } from "./actions";
import { GET_GAMES } from "./actions";
import { CREATE_GAME } from "./actions";
import { GAME_DETAIL } from "./actions";
import { ORDER_GAMES } from "./actions";
import { ORDER_RATING } from "./actions";

const initialState = {
  allGames: [],
  gameDetail: {},
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_GAMES:
      return {
        ...state,
        allGames: action.payload,
      };

    case GAME_DETAIL:
      return {
        ...state,
        gameDetail: action.payload,
      };

    case FILTER_GENRES:
      let filteredGenres = [...state.allGames];
      filteredGenres =
        action.payload === "All"
          ? state.allGames
          : state.allGames.filter((game) => game.genres === action.payload);
      return {
        ...state,
        allGames: filteredGenres,
      };

    case CREATE_GAME:
      return {
        ...state,
        allGames: [...state.allGames, action.payload],
      };

    case ORDER_GAMES:
      let sortedGames = [...state.allGames];

      sortedGames.sort((a, b) => {
        if (action.payload === "Ascendente") {
          return a.name.localeCompare(b.name);
        } else {
          return b.name.localeCompare(a.name);
        }
      });

      // sortedGames =
      //   action.payload === "Ascendente"
      //     ? (state.allGames = state.allGames?.sort((a, b) => a.id - b.id))
      //     : (state.allGames = state.allGames?.sort((a, b) => b.id - a.id));

      return {
        ...state,
        allGames: sortedGames,
      };

    case ORDER_RATING:
      let orderRat = [...state.allGames];

      orderRat.sort((a, b) => {
        if (action.payload === "0-5") {
          return a.rating - b.rating;
        } else {
          return b.rating - a.rating;
        }
      });

      return {};

    default:
      return { ...state };
  }
};
export default rootReducer;
