const validate = (indication) => {
  const regex = /^[a-zA-Z_][a-zA-Z0-9_]*$/;
  const errors = {};
  if (indication.name.length > 0 && !regex.test(indication.name)) {
    errors.name = "invalid name";
  }
};
export default validate;
