import axios from "axios";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import * as action from "../../Redux/actions";

export const Detail = () => {
  const params = useParams();
  const gameDetail = useSelector((state) => state.gameDetail);
  const dispatch = useDispatch();

  const [games, setGames] = useState([]);

  useEffect(() => {
    dispatch(action.gameDetails(params.id));
  }, []);

  // useEffect(() => {
  //   axios
  //     .get(`http://localhost:3001/videogames/${params.id}`)
  //     .then((response) => setGames(response.data));
  // }, []);

  return (
    <div>
      {games.name ? (
        <div>
          <h2>Id: {games.id} </h2>
          <h2>Name: {games.name} </h2>
          <h2>Platforms: {games.platforms}</h2>
          <h2>Description: {games.description}</h2>
          <h2>Released: {games.released}</h2>
          <h2>Rating: {games.rating}</h2>
          <h2>Genres: {games.genres}</h2>
          <img src={games.background_image} alt="" />
        </div>
      ) : (
        <div>
          <h2>...Loading</h2>
        </div>
      )}
      <Link to="/Home">
        <button>Home</button>
      </Link>
    </div>
  );
};
