import { Link } from "react-router-dom";
import { SearchBar } from "../SearchBar/SearchBar";
import { useDispatch, useSelector } from "react-redux";
import {
  filterGenres,
  orderGames,
  orderRating,
  getGenres,
} from "../../Redux/actions";
import { useEffect } from "react";

export const Nav = ({ onSearch }) => {
  const dispatch = useDispatch();
  const allGenres = useSelector((state) => state.allGenres);

  const handleChangeGenre1 = (event) => {
    dispatch(orderGames(event.target.value));
    console.log(dispatch(orderGames(event.target.value)));
  };

  const handleChangeGenre2 = (event) => {
    dispatch(filterGenres(event.target.value));
  };

  const handleChangeGenre5 = (event) => {
    dispatch(orderRating(event.target.value));
  };

  useEffect(() => {
    dispatch(getGenres());
  }, []);

  return (
    <div>
      <Link to="/videogames/createGames">
        <button>Crear Videojuego</button>
      </Link>
      <SearchBar onSearch={onSearch} />

      <div>
        <label htmlFor="">Order by: </label>

        {/* <select name="ordenamiento" id="" onChange={handleChangeGenre1}>
          <option value="Ascendente">Ascendente</option>
          <option value="Descendente">Descendente</option>
        </select> */}

        <label htmlFor="">Filter by Genres: </label>
        <select name="filtro" id="" onChange={handleChangeGenre2}>
          <option value="All">All</option>
          {allGenres.map((genre) => (
            <option key={genre.id} value={genre.name}>
              {genre.name}
            </option>
          ))}
        </select>

        <label htmlFor="">Order Alphabetic: </label>
        <select onChange={handleChangeGenre1}>
          <option value="A-Z">A-Z</option>
          <option value="Z-A">Z-A</option>
        </select>

        <label htmlFor="">Filter by created: </label>
        <select>
          <option value="All">All</option>
          <option value="Created">Created</option>
          <option value="Not Created">Not created</option>
        </select>

        <label>Order rating: </label>
        <select onChange={handleChangeGenre5}>
          <option value="none">none</option>
          <option value="0-5">0-5</option>
          <option value="5-0">5-0</option>
        </select>
      </div>
    </div>
  );
};
