import { Card } from "../Card/Card";
import style from "../Home/Home.module.css";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { useState } from "react";

const [games, setGames] = useState([]);

export const onSearch = (name) => {
  const URL_BASE = "http://localhost:3001/videogames";

  if (games?.find((game) => game.name === name)) alert("Personaje repetido");

  axios.get(`${URL_BASE}?name=${name}`).then((response) => {
    if (response.data.length > 0) {
      setGames((oldGames) => [...oldGames, ...response.data]);
    } else {
      alert("Algo salio mal");
    }
  });
};

export const Home2 = () => {
  return (
    <div className={style.containt}>
      {games?.map(({ id, name, background_image, genres }) => {
        return (
          <Card
            key={id}
            id={id}
            name={name}
            background_image={background_image}
            genres={genres}
          />
        );
      })}
    </div>
  );
};
