import React from "react";
import "./App.css";
import { Routes, Route, useLocation } from "react-router-dom";
import { Home } from "./components/Home/Home";
import { Start } from "./components/Start/Start";
import { Nav } from "./components/Nav/Nav";
import { Detail } from "./components/views/Detail";
import { CreateGame } from "./components/CreateGame/CreateGame";
import axios from "axios";
import { useState } from "react";

function App() {
  const location = useLocation();
  const [games, setGames] = useState([]);

  const onSearch = (name) => {
    const URL_BASE = "http://localhost:3001/videogames";

    if (games?.find((game) => game.name === name)) alert("Personaje repetido");

    axios.get(`${URL_BASE}?name=${name}`).then((response) => {
      if (response.data.length > 0) {
        setGames((oldGames) => [...oldGames, ...response.data]);
      } else {
        alert("Algo salio mal");
      }
    });
  };

  return (
    <div className="App">
      {location.pathname !== "/" && <Nav onSearch={onSearch} />}
      <Routes>
        <Route path="/" element={<Start />} />
        <Route path="/Home" element={<Home games={games} />} />
        <Route path="/Detail/:id" element={<Detail />} />
        <Route path="/videogames/createGames" element={<CreateGame />} />
      </Routes>
    </div>
  );
}

export default App;
