import "./App.css";
import { Switch, Route, useLocation } from "react-router-dom";
import { Home } from "./components/Home/Home";
import { Start } from "./components/Start/Start";
import { Nav } from "./components/Nav/Nav";
import { Cards } from "./components/Cards.jsx/Cards";
import { useState } from "react";
import axios from "axios";

function App() {
  const loccation = useLocation();
  const [characters, setCharacters] = useState([]);

  const onSearch = (name) => {
    const URL_BASE = "http://localhost:3001/videogames";

    if (characters.find((char) => char.name === name))
      alert("Personaje repetido");

    axios.get(`${URL_BASE}?name=${name}`).then((response) => {
      if (response.data) {
        setCharacters((oldChars) => [...oldChars, response.data]);
      } else {
        alert("Algo salio mal");
      }
    });
  };

  return (
    <div className="App">
      {loccation.pathname !== "/" && <Nav onSearch={onSearch} />}
      <Switch>
        <Route exact path="/" component={Start} />
        <Route exact path="/Home" component={Home} />
      </Switch>
    </div>
  );
}

export default App;
