import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import { gameDetails, cleanDetail } from "../../Redux/actions";
import style from "../views/Detail.module.css";

export const Detail = () => {
  const { id } = useParams();
  const gameDetail = useSelector((state) => state.gameDetail);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(cleanDetail());
    dispatch(gameDetails(id));
  }, [dispatch]);

  return (
    <div className={style.containt}>
      {gameDetail.name ? (
        <div className={style.detalles}>
          <h2>Id: {gameDetail.id} </h2>
          <h2>Name: {gameDetail.name} </h2>
          <h2>
            Description: {gameDetail.description.replace(/<[^>]*>?/g, "")}
          </h2>
          <div className={style.feactures}>
            <h2>Platforms: {gameDetail.platforms}</h2>
            <h2>Released: {gameDetail.released}</h2>
            <h2>Rating: {gameDetail.rating}</h2>
            <h2>
              Genres:
              {!gameDetail.createdInDb
                ? gameDetail.genres
                : gameDetail.Genres.map((elem) => elem.name)}
            </h2>
          </div>
          <img src={gameDetail.background_image} alt="" />
        </div>
      ) : (
        <div>
          <h2>...Loading</h2>
        </div>
      )}
      <Link to="/Home">
        <button>Home</button>
      </Link>
    </div>
  );
};
