import axios from "axios";
export const FILTER_GENRES = "FILTER_GENRES";
export const GET_GAMES = "GET_GAMES";

export const filterGenres = (genres) => {
  return { action: FILTER_GENRES, payload: genres };
};

export const getGames = () => {
  return function (dispatch) {
    axios.get("http://localhost:3001/videogames").then((response) => {
      return { type: GET_GAMES, payload: response.data };
    });
  };
  // setJuegos((oldJuegos) => [...oldJuegos, ...data]);
};
