import { FILTER_GENRES } from "./actions";
import { GET_GAMES } from "./actions";
import { CREATE_GAME } from "./actions";
import { GAME_DETAIL } from "./actions";
import { ORDER_GAMES } from "./actions";

const initialState = {
  allGames: [],
  gameDetail: {},
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_GAMES:
      return {
        ...state,
        allGames: action.payload,
      };

    case GAME_DETAIL:
      return {
        ...state,
        gameDetail: action.payload,
      };

    case FILTER_GENRES:
      const filteredGenres =
        action.payload === "All"
          ? state.allGames
          : state.allGames.filter((game) => game.genres === action.payload);
      return {
        ...state,
        allGames: filteredGenres,
      };

    case CREATE_GAME:
      return {
        ...state,
        allGames: [...state.allGames, action.payload],
      };

    case ORDER_GAMES:
      const gamesOrder =
        action.payload === "Ascendente"
          ? (state.allGames = state.allGames?.sort((a, b) => a.id - b.id))
          : (state.allGames = state.allGames?.sort((a, b) => b.id - a.id));

      console.log(state.allGames.id);

      return {
        ...state,
        allGames: gamesOrder,
      };
    default:
      return { ...state };
  }
};
export default rootReducer;
