import { useEffect, useState } from "react";
import { Card } from "../Card/Card";
import style from "../Home/Home.module.css";
import { Home2 } from "../Home2/Home2";
import { useDispatch, useSelector } from "react-redux";
import { getGames } from "../../Redux/actions";
import Paginado from "./Paginado";

export const Home = ({ games }) => {
  const allgames = useSelector((state) => state.allGames);
  const dispatch = useDispatch();

  const [currentPage, setCurrentPage] = useState(1); // Pagina Acutal

  const [videoJuegosPorPagina, setVideoJuegosPorPagina] = useState(15); // video juegos por pagina

  const indexOfLastGames = currentPage * videoJuegosPorPagina; //15 que es el indice del ultimo video juego

  const indexOfFirstGames = indexOfLastGames - videoJuegosPorPagina; //0 que es el indice del primer video juego

  const currentVideogames = allgames.slice(indexOfFirstGames, indexOfLastGames); // los videojuegos que se mostraran por pagina

  const [order, setOrder] = useState("");

  const paginado = (numPage) => {
    setCurrentPage(numPage);
  };

  useEffect(() => {
    dispatch(getGames());
  }, []);

  return (
    <div className={style.containt}>
      {allgames?.map(({ id, name, background_image, genres }) => {
        return (
          <Card
            key={id}
            id={id}
            name={name}
            background_image={background_image}
            genres={genres}
          />
        );
      })}
      <Home2 games={games} />
      {/* <div>
        <Paginado
          allGames={allgames.length}
          videoJuegosPorPagina={videoJuegosPorPagina}
          paginado={paginado}
        />
        {currentVideogames?.map((elem) => {
          return (
            <Card
              key={elem.id}
              id={elem.id}
              name={elem.name}
              image={elem.image}
            />
          );
        })}
      </div> */}
    </div>
  );
};
