import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createGame } from "../../Redux/actions";
import validate from "./validate";

export const CreateGame = () => {
  const dispatch = useDispatch();
  const allGenres = useSelector((state) => state.allGenres);

  const [indication, setIndication] = useState({
    name: "",
    image: "",
    description: "",
    platforms: [],
    released: "",
    rating: 0,
    genres: [],
  });

  const [errors, setErrors] = useState({
    name: "",
    image: "",
    description: "",
    platforms: [],
    released: "",
    rating: 0,
    genres: [],
  });

  const handleInputChange = (event) => {
    const property = event.target.name;
    const value = event.target.value;

    setIndication({ ...indication, [property]: value });

    setErrors(validate({ ...indication, [property]: value }, errors));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(createGame(indication));
  };

  console.log(indication);

  return (
    <div>
      <form action="" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="">Nombre:</label>
          <input
            type="text"
            name="name"
            value={indication.name}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="">Image:</label>
          <input
            type="text"
            name="image"
            value={indication.image}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="">Description:</label>
          <textarea
            name="description"
            value={indication.description}
            onChange={handleInputChange}
            id=""
            cols="30"
            rows="10"
          ></textarea>
        </div>
        <div>
          <label htmlFor="">platforms:</label>
          <select
            name="platforms"
            value={indication.platforms}
            onChange={handleInputChange}
          >
            <option value="Select">Select</option>
            <option value="PC (Microsoft Windows)">
              PC (Microsoft Windows)
            </option>
            <option value="PlayStation 4">PlayStation 4</option>
            <option value="Xbox One">Xbox One</option>
            <option value="Nintendo Switch">Nintendo Switch</option>
            <option value="iOS">iOS</option>
            <option value="Android">Android</option>
            <option value="Xbox Series X|S">Xbox Series X|S</option>
            <option value="PlayStation 5">PlayStation 5</option>
            <option value="macOS">macOS</option>
            <option value="Linux">Linux</option>
            <option value="Nintendo 3DS">Nintendo 3DS</option>
            <option value="Nintendo DS">Nintendo DS</option>
            <option value="Xbox 360">Xbox 360</option>
            <option value="Xbox 360">Xbox 360</option>
            <option value="PlayStation 3">PlayStation 3</option>
            <option value="PlayStation 2">PlayStation 2</option>
            <option value="PlayStation">PlayStation</option>
            <option value="PlayStation Portable">PlayStation Portable</option>
            <option value="PlayStation Vita">PlayStation Vita</option>
            <option value="Wii">Wii</option>
            <option value="Wii U">Wii U</option>
            <option value="Game Boy Advance">Game Boy Advance</option>
            <option value="Game Boy Color">Game Boy Color</option>
            <option value="Game Boy">Game Boy</option>
            <option value="SNES">SNES</option>
            <option value="NES">NES</option>
            <option value="N64">N64</option>
            <option value="GameCube">GameCube</option>
            <option value="SEGA Genesis">SEGA Genesis</option>
            <option value="SEGA Mega Drive">SEGA Mega Drive</option>
            <option value="SEGA CD">SEGA CD</option>
            <option value="SEGA Saturn">SEGA Saturn</option>
            <option value="Dreamcast">Dreamcast</option>
          </select>
        </div>
        <div>
          <label htmlFor="">Released:</label>
          <input
            type="date"
            name="released"
            value={indication.released}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label htmlFor="">Rating:</label>
          <input
            type="number"
            name="rating"
            value={indication.rating}
            onChange={handleInputChange}
          />
        </div>

        <div>
          <label htmlFor="">Genres:</label>

          <select
            name="genres"
            value={indication.genres}
            onChange={handleInputChange}
          >
            <option value="All">All</option>
            {allGenres.map((genre) => {
              <option key={genre.id} value={genre.name}>
                {genre.name}
              </option>;
            })}
          </select>
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

